css:
@-moz-document domain("www.thebluealliance.com") {
body {
	color: #ccc;
	background-color: #333;
}
table.match-table, table.oprTable {
	background-color: #333;
}
table.match-table td.red {
	background-color: #e85d5d;
}
table.match-table td.blue {
	background-color: #3e3edc;
}
a {
	color: #fff;
}

a:hover {
	color: #ccc;
}

table.match-table>thead>tr>th, table.match-table>tbody>tr>th, table.match-table>tfoot>tr>th, table.match-table>thead>tr>td, table.match-table>tbody>tr>td, table.match-table>tfoot>tr>td {
	border: 1px solid #000;
	background-color: #444;
}
table.match-table .winner {
	font-weight: bold;
	color: yellow;
}
table.match-table td.redScore {
	background-color: #e85d5d;
}
table.match-table td.blueScore {
	background-color: #3e3edc;
}
.table-striped>tbody>tr:nth-of-type(odd) {
	background-color: #635e5e;
}
.brackettable .secondC {
	background-color: #555;
}
.brackettable .winnerC {
	background-color: #444;
}
.brackettable .winnerL {
	background-color: #444;
}
.brackettable .winnerR {
	background-color: #444;
}
.brackettable tr td {
	background-color: #555;
}
.event-table>tbody>tr:nth-of-type(odd) {
	background-color: #555;
}

.panel {
	background-color: #555;
}

.panel-heading {
	background-color: #555;
}

.panel-title {
	color: #ccc;
}

.panel-default>.panel-heading {
    color: #333;
    background-color: #444;
    border-color: #ddd;
}

.panel-default {
	border-color: #666;
	background-color: #555;
}

.tba-sidenav {
	background-color: #555;
	text-shadow: none;
}

.tba-sidebar .nav>li>a {
    color: #fff;
}

.nav>li>a:hover, .nav>li>a:focus {
    background-color: #666;
}
}
