css:
@-moz-document domain("lichess.org") {
.purple .is2d .cg-board,
#top div.color_demo.purple {

background-image:url("http://i.imgur.com/Axdp1CP.png")  !important;

}

piece.pawn.black { background-image: url("http://imgh.us/Classic_(4).svg")!important;} 
	piece.knight.black { background-image: url("http://imgh.us/Classic_(3).svg")!important;} 
	piece.bishop.black { background-image: url("http://imgh.us/Classic_(1).svg")!important;} 
	piece.rook.black { background-image: url("http://imgh.us/Classic_(6).svg")!important;} 
	piece.queen.black { background-image: url("http://imgh.us/Classic_(5).svg")!important;} 
	piece.king.black { background-image: url("http://imgh.us/Classic_(2).svg")!important;} 

	piece.pawn.white { background-image: url("http://imgh.us/Classic_(10).svg")!important;} 
	piece.knight.white { background-image: url("http://imgh.us/Classic_(9).svg")!important;} 
	piece.bishop.white { background-image: url("http://imgh.us/Classic_(7).svg")!important;} 
	piece.rook.white { background-image: url("http://imgh.us/Classic_(12).svg")!important;} 
	piece.queen.white { background-image: url("http://imgh.us/Classic_(11).svg")!important;} 
	piece.king.white { background-image: url("http://imgh.us/Classic_(8).svg")!important;}

.cg-square.last-move { background-color:rgba(255, 255, 0, 0.41) !important;} 

.canvas .is2d .cg-board, #top div.color_demo.canvas{

background-image: url(http://i.imgur.com/GGINZ8U.jpg) ;
}

.cg-board-wrap coords {
    text-transform: lowercase;
    font-family: 'Calibri',sans-serif;
    font-size: 22px;
    font-weight: bold;
    color: black;
    
}

.cg-board-wrap coords.files {
    text-transform: lowercase;
}

.lichess_board {
	
    border: 32px solid #6282B7 !important;
}
.underboard {
	margin-top: 60px !important;
}
.ranks coord{
    margin-left: 8px !important;
}
.files coord{
    margin-top: 2px !important;
}
}
