css:
@-moz-document domain("app.hackerwebapp.com") {
/* with <3, by tzekid */
/* you can check the code @ tzekid/stylish_styles */
body { background-color: #333; }
header { background-color: #333; }

/* Home */
.tableview { background-color: #333; }
.tableview-links li>a { color: #eee; }
.tableview-links li>a:hover { background-color: #444; }
.tableview li { border-color: #ff6600; }
/* .view>header { border-color: #ff6600; } */
.view>header { border: none; }

/* Post */
.view>header { background-color: #333; }
.view>header h1 { color: #eee; }
.view .post-content header h1 { color: #eee; }
/*.view section.comments { background-color: #333; border-color: #ff6600; }*/
 .view section.comments { background-color: #333; border:none; }
.header-button:hover, .view.shaded>.scroll { background-color: #333; }
.view section.comments p { color: #fff; }
.view section.comments p.metadata .user { color: #ff6600; }
.link-text { color: #999; }
.view section.comments ul li { border-color: #ff6600; }
/*.view .post-content { border-color: #ff6600; }*/
 view .post-content { border: none; } 

/* About */
.grouped-tableview { background-color: #333; }
#app-desc strong { color: #eee; }
ol.grouped-tableview-links li>a, ul.grouped-tableview-links li>a {
  color: #eee;
}
.view.shaded>.scroll p.foot-label { color: #999; }
.grouped-tableview p { color: #999; }
}
