css:
@-moz-document domain(start.me) {
  .widget .body  {
      box-sizing: border-box;
      padding: 0 6px;
      background: rgba(15, 15, 15, .7) !important;
      margin: 1px 1px !important;
      border-radius: 5px;
  }
  .urllist-link .title {
      color: white !important;
      font-size: 12px !important;
      font-weight: bold !important;
  }
  .urllist-link:hover {
      background: rgba(250, 250, 250, .1) !important;
  }
  .view-only.conditional-visibility-toggle {
      color: #aaa !important;
  }
  .view-only.itemlist.weather-forecasts {
      color: white !important;
  }
  .view-only.itemlist.weather-forecasts a {
      color: lightblue;
  }
  .widget {
      padding-bottom: 0 !important;
      border-radius: 5px !important;
  }
  .task-list {
      color: white !important;
  }
}
