css:
@-moz-document domain("bethesda.net") {
blockquote {
    color: #b1b1b1 !important;
    background-color: #565656 !important;
}
.topic-title {
    background-color: #787878 !important;
    width: 100% !important;
    margin-left: 0px !important;
}
.topic {
    width: 100% !important;
    max-width: 1900px !important;
    margin-left: auto !important;
    margin-right: auto !important;
}
.post-row:nth-child(odd) {
    background-color: #151515 !important;
}
.post-row:nth-child(even) {
    background-color: #373737 !important;
    border-top: 20px !important;
}
body.dark .topic .post-row .post-body {
    border-bottom: 30px solid !important;
    border-bottom-color: #a00b0b !important;
    margin-left: auto !important;
}
.post-content {
    min-height: 200px !important;
}
.op-tools [component="post/upvote"] {
    display: none !important;
}
.post-tools [component="post/upvote"] {
    display: none !important;
}
}
