@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.dn.se") {
.page__grid {
    width: 1656px;}

.page__grid, .section-sidebar__grid {
    background: transparent !important;}

.footer, .header {
    max-width: 1390px;}

.article__lead, .article__premium-content > *, .article__body-content, .article__limited-width, .article__bottom {
    max-width: 1100px;}

.byline__body {
    min-width: 800px;} 

.article__body .factbox--review, .article-teaser-list--article-tags {
    min-width: 800px;
    float: left;}

.article .article-teaser-list--article-tags {
    float: left;}

.article__body, .article--longread .article__body-content > * {
    min-width: 1100px !important;}

.tagpage .teaser-list--timeline > .teaser-list__item > a > .article-teaser > .article-teaser__main-container > .article-teaser__content {
    float: left !important;}

.image-box__caption{
    text-align: center;}

.ad--article-bottom {
    border-bottom: solid 1px transparent;}

.ad--panorama:first-of-type .ad-space-container {
    min-height: 0px;}

.account-information--visible, .article-promo, .sponsored-links, #header-ad, #siteContentThreeColRight, .adtoma, .enirosponslink, #header-verticals, .topad, .ad--panorama .ad-header:not(.ad-header--hidden), .ad--skyscraper .ad-header:not(.ad-header--hidden), .ad--section-inline, .ad--article-inline, .ad--widget,.ad .ad-container, .ad--sticky-sidebar, .box-advert, .box-advert-inner, #pwOverlay, .account-information__tooltip.js-account-information, .takeover, .bottom-bar {
    display: none !important;}
}
