css:
@-moz-document domain("vk.com") {
:root {
	--bg: black;
	--bga: #68a3;
	--bgh: #2466;
	--bgm: #4686;
	--color: white;
}

* {
	box-shadow: none !important;
}


body {
	background: var(--color);
}
.audio_layer_bottom,
.im-chat-input,
.im-page_classic.im-page .im-chat-history-resize,
.stl_active.over_fast #stl_bg,
.submit_post,
.ui_tabs_header,
.wr_header {
	background: transparent;
}

/* borders */
#ads_left.ads_left_empty + .left_menu_nav_wrap,
#page_header_cont .back,
#side_bar .more_div,
#top_notify_wrap,
.counts_module,
.deep_active .replies .replies_list,
.deep_active .replies .replies_wrap_deep,
.deep_active .replies .reply .reply_wrap,
.eltt,
.deep_active .post_replies_header,
.flat_button.ui_load_more_btn,
.group_list_row,
.im-page_classic.im-page .im-page--header,
.im-page_classic.im-page .im-page--chat-input,
.im-page_classic .im-page--chat-header,
.im-page--toolsw,
.like_cont,
.nim-dialog .nim-dialog--content,
.post_full_like_wrap,
.ui_search,
.ui_rmenu_sep,
.wall_module .reply_box,
.wall_module .reply_fakebox,
.wide_column .page_top {
	border: none;
}

.wall_post_text,
.im_msg_text,
.im-mess--text,
.bp_text {
	border: none;
	font-family: Georgia;
	font-size: 1.2rem !important;
	line-height: 1.4 !important;
	padding: 0;
}
.wall_module .copy_quote {
	border-left-width: 1px;
}
.im-page_classic.im-page .im-page--chat-body-wrap-inner {
	border-bottom-color: transparent;
}
#page_header_cont .back {
	background: var(--bga);
}
.top_audio_player .top_audio_player_title,
input.text.ts_input,
.ts_wrap .input_back_content {
	background: none !important;
	color: var(--color) !important;
}
.top_nav_btn.active,
.top_nav_btn:hover {
	background-color: var(--bgm);
}
.im-page--toolsw,
.im-page--chat-header {
	background-color: #fff7 !important;
}

/* comments */
.wall_reply_text {
	font-size: 1.3rem;
	font-family: Georgia;
}

.post_field_user_image._post_field_image {
	display: none;
}

/* stories */
.article_layer__content p {
	font-size: 1.5rem;
	line-height: 1.3
}
}
