css:
@-moz-document domain("www.hades-blumenservice.de") {
body {
    background-color: rgb(30, 30, 30);
    color: rgb(160, 160, 160);
}

label,
.sidebar .toolbox-filter {
    color: rgb(160, 160, 160);
}

div {
    background-color: rgb(46, 46, 46);
}

.clipboard,
.sidebar-footer {
    display: none;
}

.sidebar .sidebar-content,
.sidebar .storage,
.box .boxBody,
.document-page,
.area,
.navbar-fixed-top,
.footer,
.matrix-monitor {
    background-color: rgb(46, 46, 46);
}

input,
select,
textarea {
    background-color: rgb(46, 46, 46);
    border: 1px solid #d5d5d5;
    border-color: rgb(62, 62, 62);
}

.pages .active {
    background-color: rgb(62, 62, 62);
}

.box .box-toolbox li {
    /*box-shadow: rgb(103, 103, 103) 0px 0px 3px inset, rgba(0, 0, 0, 0.2) 0px 0px 0px 1px, rgba(0, 0, 0, 0.298039) 0px 1px 3px;*/
    
    background: linear-gradient(rgb(71, 71, 71), rgb(43, 43, 43)) rgb(59, 59, 59) !important;
    border-color: rgb(85, 85, 85) !important;
    padding: 0px;
}

.box .box-toolbox {
    top: 1px;
    right: 2px;
}

.slider {
    z-index: 100;
}

.content-container {
    margin-left: auto;
    margin-right: auto;
    position: static;
}

.anchor {
    margin-top: 30px;
}

.storage {
    height: 74vh !important;
}
}

@-moz-document domain("www.hades-blumenservice.de") {
::-webkit-scrollbar {
    background-color: rgb(46, 46, 46);
    width: 10px;
}

::-webkit-scrollbar-thumb {
    background-color: rgb(62, 62, 62);
}

::-webkit-scrollbar-corner {
    background-color: rgb(46, 46, 46);
}
}
