css:
@-moz-document regexp("https?://(www\\.)?speedrun\\.com/.*") {
/* better category tabs */
  .maincontent > ul.nav-tabs { display: flex; flex-wrap: wrap; box-shadow: 3px 3px 5px rgba(8,8,8,0.4) }
  .maincontent > ul.nav-tabs > li, #miscellaneous ul li { flex-grow: 1; text-align: center; min-width: 15%; }
  .maincontent > ul.nav-tabs > li > a, #miscellaneous ul li a { border-radius: 0; border-bottom-width: 3px !important; }
  .maincontent > ul.nav-tabs > li.active > a, #miscellaneous ul li.active a,
  .maincontent > ul.nav-tabs > li:hover > a, #miscellaneous ul li:hover a { border-bottom-color: #FFF !important; }
  .maincontent > .panel-tabbed { border-radius: 0; }

/* better misc categories dropdown */
  #miscellaneous ul { min-width: 100%; box-shadow: none !important; border: none; }
}
