css:
@-moz-document domain("canardpc.com") {
body, #block-views-block-home-page-carrousel-a-la-une-block-1, #block-homepagecarrousel, #block-numerocarrousel, #block-gratuitcarrousel {
    background-color: pink;
}

h1, h2, h3, h4 {
    color: blue;
    font-family: comic-sans !important;
}

.node--type-article--wrapper .categorie {
    background-color : white;
    border: 5px dotted #aa00CD;
    border-radius: 10px;
    font-weight: bold;
}

.node--type-article--wrapper .categorie.gratuit {
   background-color: cyan; 
}

.node--type-article--wrapper .categorie.gratuit::after{
    content: "gratos!!!";
    animation: 0.2s gratos infinite step-end;
}
@keyframes gratos{
    from{color:red;}
    50%{color: white;}
    to{color:white;}
} 

#block-sommaire, #block-sommairesuivant, #block-numerosommaire {
    background-color: yellow;
    background-image: none;
}

.region-header {
    animation: 2s infinite header linear;
}
@keyframes header{
    from{background-color:#f00;}
    16.5%{background-color:#ff0;}
    33%{background-color:#0f0;}
    49.5%{background-color:#0ff;}
    66%{background-color:#00f;}
    82.5%{background-color:#ff0;}
    to{background-color:#f00;}
}
    
a:hover{
  animation: 0.5s wizz  infinite linear !important;
}
@keyframes wizz {
    from{transform: rotate(0deg);}
    to{transform: rotate(360deg);}
}
    
   
#cboxLoadedContent{
    background-color : cyan;
            
}
#colorbox {
 	height:100% !important;
    width: 150% !important;
    position: fixed !important;
    top: 0px !important;
    left: 0px !important;
    bottom: 0px !important;
    right: 0px !important;
}
#cboxWrapper {
    height: inherit !important;
    width: inherit !important;
} 

div {
    background-color: rgba(0,255,255,0.05);
    border-radius: 30px;
}
}
