css:
@-moz-document domain("www.youtube.com") {
.account-feature-v2-grid-items li.account-features-v2-grid-item-grey {
    border-bottom: solid 3px #ff0000;
}
.account-feature-v2-grid-items li.account-features-v2-grid-item-green {
    border-bottom: solid 3px #ff0000;
}
.account-status-copyright-status-v2-scale .status-scale-great, .account-status-community-guidelines-status-v2-scale .status-scale-great {
    background: no-repeat url(https://s.ytimg.com/yts/imgbin/www-accountsettings-vfle0tTtG.webp) -91px -66px;
}
.account-status-copyright-status-v2-details:after {
    position: absolute;
    right: 236px;
    content: "You have 2 copyright strikes.";
    visibility: visible;
}
.account-status-copyright-status-v2-details {
    visibility: hidden;
	position: relative;
}
.account-status-community-guidelines-status-v2-details{
        visibility: hidden;
	position: relative;
}
.account-status-community-guidelines-status-v2-details:after {
    position: absolute;
    right: 163px;
    visibility: visible;
    content: "You have 3 Community Guidelines strikes.";
}
.account-feature-v2-grid-items .account-features-v2-grid-item-status{
    visibility: hidden;
	position: relative;
}
.account-feature-v2-grid-items .account-features-v2-grid-item-status:after{
    position: absolute;
    padding: 5px 5px 10px 15px;
    bottom: 0;
    display: inline-block;
    left: 0;
    top: 49px;
    right: 10px;
    font-size: 14px;
    font-weight: 500;
    visibility: visible;
    content: "Revoked";
}
.account-features-v2-grid-item-description {
    visibility: hidden;
	position: relative;
}
.account-features-v2-grid-item-description:after {
    position: absolute;
    display: inline-block;
    height: 55px;
    left: 0;
    bottom: 0;
    visibility: visible;
    content: "This feature has been disabled due to your channel status!";
}
}
