css:
@-moz-document domain("w.amazon.com") {
#contentcontainer {
  max-width: 980px;
  margin: 2rem auto 0 auto;
  border-radius: 5px;
  box-shadow: 0 1px 5px 1px rgba(0,0,0,0.1);
}

.main {
  border-radius: 5px;
  padding-top: 30px;
}

.container-fluid {
  max-width: 1010px;
}

#data-warning-box,
#footerglobal {
  display: none;
}

#xwikimaincontainerinner {
  padding-bottom: 30px;
}

h1, .h1, h2, .h2, h3, .h3 {
    margin-top: 30px;
    margin-bottom: 15px;
    border-bottom: none !important;
}

.breadcrumb {
    background-color: rgba(229, 246, 251, 0.54);
    border-radius: 5px 5px 0 0;
    margin-top: -30px;
    margin-left: -30px;
    margin-right: -30px;
}
}
