css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("6mchess.com") {
  body {
    background-image: url("https://images.chesscomfiles.com/uploads/theme/101411-0.2d687d92.jpg");
    background-attachment: fixed;
    background-size: cover;
    font-family: geneva, "Comic Sans MS";
  }
  .button.large, .mainMenu, .dropDownContent a {
    box-shadow: none !important;
  }
  .button.large, .dropDownContent a, .collectionEntry, .tab:hover, .mainMenu a:link {
    transition: .25s ease;
    color: #fff !important;
  }
  .button:hover, .dropDownContent a:hover, .spacing,.historyEntry:hover,.collectionEntry:hover, .tab:hover, .mainMenu a:link:hover {
    background: #924B1D;
}
  .button, .mainMenu, .dropDownContent a, .activeTab {
    background: #D7A25D;
}
  .blue .cg-board {
     background-image: url("https://lichess1.org/assets/images/board/wood-1024.jpg");
  }
  .collectionEntry, .activeTab, .tab {
    border: none;
  }
  input,textarea {
    background: transparent;
    background: RGBa(0,0,0,0.2);
    border: 1px solid #fff;
    color: #fff;
    padding: 15px;
    font-size: 14px;
    transition: 0.25s ease;
    font-family: sans-serif;
    font-weight: 100;
  }
  input:focus,textarea:focus {
    border: 1px solid #1D628B;
    background: RGBa(0,0,0,0.5);
  }
  .historyContent{
    margin-bottom: 50px;
  }
}
