css:
@-moz-document domain("voffka.com") {
.blogbody,
body,
li,
p,
td {
  font-family: Georgia;
  font-size: 1.3rem;
  line-height: 1.4 !important;
  border: none;
  padding: 0;
}

body {
  padding-right: 10%;
}

h1.title.entry-title,
h3.title {
  margin-top: 70px;
  font-family: "Open Sans", "Helvetica", Arial, sans-serif;
  font-size: 2rem !important;
  font-weight: 100;
  text-transform: none;
}

#entry-banner,
.row {
  margin-top: -150px;
}

#logo-blk {
  position: fixed;
}

#from-right-side,
#logo-blk>*:not(:first-child),
#placement-bottom,
#shapka-blk,
#topbar,
.banner-after-menu,
.comments-head,
.comments-head,
.date,
.gravatar,
.mailform,
.mailform,
.nav-links,
.right-banner,
.uptl_container,
aside#links,
br+br,
script[src*="/js/"],
tbody {
  display: none !important;
}

.title {
  font-size: 1.8rem !important;
  font-weight: normal;
  margin-top: 1em;
}

#disqus_thread,
.blog-w-right-banner {
  margin-left: 200px;
}

.posted {
  font-family: sans-serif;
  font-size: .7rem;
  padding: 0;
}

.caliber {
  color: transparent !important;
}

.caliber a {
  padding: 7px 10px;
  border: 1px solid #eee;
  font-family: sans-serif;
  color: black !important;
}
}
