css:
@-moz-document domain("moviechat.org") {
body {
	color: #d8d8d8;
	background: #232323;
}

.main {
	background: #2f2f2f;
}

.form-control {
    color: #d8d8d8;
    background-color: #454545;
}

.trending-metadata {
    color: #d8d8d8;
}

a:focus, a:hover {
    color: #00b2ea;
}

a {
    color: #0089ff;
}

.movie-overview {
    background: #3e3e3e;
}

.thread-even {
    background: #3e3e3e;
}

.board-odd {
    background: #3e3e3e;
}

.post {
    background: #3e3e3e;
}

.post-title {
    color: #d8d8d8;
}

.metadata {
    color: #9c9c9c;
}

.reply-btn {
    color: #bbbbbb;
}

.edit-btn {
    color: #bbbbbb;
}

.delete-btn {
    color: #bbbbbb;
}

.report-btn {
    color: rgba(187, 187, 187, 1);
}

.ignore-btn {
    color: rgba(187, 187, 187, 1);
}

.timestamp {
    color: #bbbbbb;
}

.pagination>li>a, .pagination>li>span {
    color: #337ab7;
    background-color: #d2d2d2;
    border: 1px solid #b3b3b3;
}

.reply {
    background: #3e3e3e;
}

.child-odd {
    background: rgb(76, 76, 76);
}

.child-even {
    background: #3e3e3e;
}

.result-even {
    background: #3e3e3e;
}
}
