css:
@-moz-document url-prefix("https://www.nicovideo.jp/watch") {
.WatchAppContainer-main>.MainContainer, .WatchAppContainer>.WatchAppContainer-main {
    display:block;
}
.WatchAppContainer-main {
    margin: 0 !important;
    width: 100% !important;
}
body {
    overflow: hidden !important;
}
.BottomContainer .BottomContainer-card:first-child {
	display:block !important;
}
.BottomContainer .BottomContainer-card {
	display:none !important;
}
.BottomContainer-side, .HeaderContainer, .SwitchToFlashLead, .FooterContainer-content, .MiniBannerAdContainer, .MarqueeContainer  {
	display:none !important;
}
#js-app {
	height: 100vh !important;
}
.BottomContainer-main {
	position: absolute !important;
    top: -200px !important;
    z-index: 1000 !important;
    width: 384px !important;
    right: 0px !important;
}
.BottomContainer-main:hover {
    width: 724px !important;
}
.WatchAppContainer, .WatchAppContainer-main, .MainContainer, .MainContainer-player, .PlayerContainer {
	height: 100% !important;
}
.VideoContainer {
	width: 100% !important;
    height: calc(100% - 80px) !important;
}
.BottomContainer {
	width: 100% !important;
}
.CommentPanelContainer {
    height: calc(100% - 220px) !important;
}

.CommentPostContainer-commandInput, .CommentInput {
	background: #000 !important;
}
input.CommentCommandInput, .CommentInput-textarea {
	color: #fff !important;
}
.CommentCommandPaletteContainer {
    background: #006ee2;
}
}
