css:
@-moz-document domain("app.roll20.net") {
.charsheet .sheet-small-textarea {height:150px;}
.charsheet .sheet-medium-textarea {
    height: 120px;
}
.charsheet textarea {
    height: 150px;
    resize:vertical;
}
#textchat-notifier {
    position: fixed;
    top: 34px;
    right: 70px;
    background-color: rgb(245, 245, 211);
    padding: 2px 10px;
    font-size: .9em;
    font-weight: bold;
    text-shadow: 1px 1px 0px #fff;
    border-radius: 0 0px 10px 10px;
    border: 1px solid hsl(45, 70%, 81%);
    border-top: 0;
    box-shadow: 1px 2px 5px rgba(101, 77, 121, 0.25);
}
.ui-widget-content a {
    color: hsla(187, 80%, 42%, 1);
    text-decoration: underline;
}
}
