css:
@-moz-document domain("www.mywriteclub.com") {
body {
    background: #262626;
}
.ta-text {
    background: #333333;
    color: #ffffff;
    font-family: Open Sans, sans-serif;
    font-size: 1em;
    line-height: 1.3em;
    text-align: justify;
}
.ta-text p {
    text-indent: 2em;
}
h1,
h2,
legend,
div.outer-container,
div.word-count,
span.sprinter-name,
button.sprinter-status,
span.sprinter-status,
span.progress-star,
div.comment-footer form input[type="Submit"] {
    color: #FFFFFF !important;
}
li.info button {
    background: #686868;
    padding: 0px 3px;
}
li.info button:hover {
    background: #808080;
    padding: 0px 3px;
}
span.help-inline {
    color: #989898;
}
div.chat-message {
    color: #333333;
}
}
