css:
@-moz-document domain("imgfoldr.com") {

html, body {
    background-color: rgb(20, 21, 24) !important;
}

h1, h2, h3, h4, p, .folder_name {
    color: rgb(242, 242, 242) !important;
    font-family: "open sans", "sans serif" !important;
}

.jumbotron, .header, .thumbnail,.media-body ,.media-left {
 	background-color: rgb(44, 47, 52) !important;
    border: 0 !important;
    border-radius: 10px;
}
    
    .footer{
       border: 0 !important;
    }
    

input {
    color: #000 !important; 
    font-family: "open sans", "sans serif" !important;
}
    
    
/* 
* ------------ Exclusions ------------
*/
    
    .header-secondary{
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    }
    
     .header{
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
    }
    
    .media-left{
        border-bottom-right-radius: 0px;
        border-top-right-radius: 0px;
    }
    
    .media-body {
    border-bottom-left-radius: 0px;
    border-top-left-radius: 0px;
}
    
}
