css:
[id$=title]
  :not([class$=description])
  :not([id$=description])
  :not(.gs-title),
[class$=title]
  :not([class$=description])
  :not([id$=description])
  :not(.gs-title){
font-size:36px!important;
line-height: 40px
}
p,
div,
input{
font-size:18px!important;
line-height: 22px
}
label{
font-size:14px!important;
line-height: 16px
}
h1{
font-size:30px!important;
line-height: 32px
}
h2{
font-size:28px!important;
line-height: 30px
}
h3{
font-size:26px!important;
line-height: 28px
}
h4{
font-size:25px!important;
line-height: 27px
}
h5{
font-size:24px!important;
line-height: 26px
}
h6{
font-size:23px!important;
line-height: 25px
}
img:not(#STTBimg){
transform: scale(1.3,1.3);
margin:1% 1%
}
button{
transform: scale(1.1,1.1)
}
p:hover,
h1:hover,
h2:hover,
h3:hover,
h4:hover,
h5:hover,
h6:hover,
td:hover,
th:hover{
font-weight:bold!important
}
tr:hover,
th:hover{
background:yellow!important;
color:black!important
}
span:hover:empty,
div:hover:empty{
font-weight:normal
}
::-moz-selection {
    color: red;
    background: yellow
}
::selection {
    color: red;
    background: yellow
}
