@-moz-document domain("lichess.org") {
.purple .is2d .cg-board, #top div.color_demo.purple  {
	background-image:url("http://i.imgur.com/FzxEWxg.png")  !important;
    
	}

piece.pawn.black { background-image: url("http://imgh.us/pawnB.svg")!important;} 
piece.knight.black { background-image: url("http://imgh.us/knight2.svg")!important;} 
piece.bishop.black { background-image: url("http://imgh.us/slonn.svg")!important;} 
piece.rook.black { background-image: url("http://imgh.us/rook_2.svg")!important;} 
piece.queen.black { background-image: url("http://imgh.us/bq_3.svg")!important;} 
piece.king.black { background-image: url("http://imgh.us/bk_5.svg")!important;} 

piece.pawn.white { background-image: url("http://imgh.us/wp_2.svg")!important;} 
piece.knight.white { background-image: url("http://imgh.us/wn_1.svg")!important;} 
piece.bishop.white { background-image: url("http://imgh.us/wb_1.svg")!important;} 
piece.rook.white { background-image: url("http://imgh.us/b54926db0d90.svg")!important;} 
piece.queen.white { background-image: url("http://imgh.us/wq_3.svg")!important;} 
piece.king.white { background-image: url("http://imgh.us/wk_4.svg")!important;}

.lichess_board {
	
    border: 16px solid #2C5738 !important;
}
.underboard {
	margin-top: 50px !important;
}
.ranks coord{
    margin-left: 2px !important;
}
.files coord{
    margin-top: 0px !important;
}

.cg-board-wrap coords {
    text-transform: lowercase;
    font-family: 'Calibri', sans-serif;
    font-size: 14px;
    font-weight: bold;
    color: gray;
}

