css:
@-moz-document domain("forum.dobreprogramy.pl") {
html {
    background: #5FB404;
}
#main-outlet {
    background: #FFFF00;
}
thead {
    background: #FFFF00;
}
.btn {
    background: #fffF00;
    border: 1px solid #ddd;
}
.submit-panel .btn,
#topic-footer-buttons .btn,
.btn-primary {
    background: #fff;
    color: #222;
}
#reply-control {
    background: #FF8000;
    border-top: 2px solid #444;
}
.nav-pills>li.active>a,
.nav-pills>li>a.active {
    background: #04a804;
}
.nav-pills>li>a:hover {
    color: #04a804;
    background-color: rgb(249, 215, 0);
}
div.topic-body {
    background: #fff;
    border: 1px solid #ddd;
}
div.topic-meta-data {
    border-bottom: 3px solid #eee;
    padding-bottom: 5px;
}
.timeline-container .topic-timeline .timeline-scrollarea {
    border-color: #3bff3a;
}
.timeline-container .topic-timeline .timeline-handle {
    background-color: #14ba14;
}
}
