css:
@-moz-document domain("www.mindworkshop.info") {
body
{
background-color: #444;
color: #eee;
}

h1, h1 a, h2, h2 a, h3, h4, h5, h6
{
	color: #acd;
}

#sidebar ul h2
{
	color: #90b0c0;
    text-shadow: 1px 1px 2px rgba(0,0,0,0.5);
}

.postmetadata
{
	color: #b0b0b0;
}

img
{
	filter: drop-shadow(0 0 10px white);
}

a, a:visited, a:hover, a:focus
{
color: #bde;
}

#content
{
background-color: rgba(255,255,255,0.25);
border-right: 230px solid rgba(255,255,255,0.13);
}

code
{
background-color: rgba(192,224,255,0.25);
color: white;
font-size: 12pt;
}

#sidebar
{
background: none transparent !important;
}
}
