css:
@-moz-document domain("myanimelist.net") {


/* SEASONAL FEATURES */

/* Default banner */
.page-common #headerSmall {
    background-image: url(https://myanimelist.net/images/mal-logo-xsmall.png) !important;
}

/* Default colors */
.page-common #menu,
.page-common #menu #menu_left {
    background: #2E51A2 !important;
}

.page-common #nav ul a,
.page-common #nav li.hover a,
.page-common .h1 {
    background: #E1E7F5 !important;
}

.page-common #nav ul li a:hover {
    background: #2e51a2 !important;
}

/* Seasonal forum threads */
.page-common #menu #menu_left #nav li a[href^="https://myanimelist.net/forum/?"] {
    display: none !important;
}

/* ADDS */
.page-common #headerSmall [class^="banner-header"],
.amazon-ads,
[data-kioskid] {
    display: none !important;
}

[style*="display: block !important"] {
    height: 0 !important;
    overflow: hidden;
}


}
