css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.cplusplus.com") {
  
  td {
    color: #ccc;
  }
  body > div > div[style^="background:#eed"] {
    display: none;
  }
  #I_mid, #I_main, #I_bottom, #I_nav .sect, #I_fold, .C_forThread .bot, .C_forPost .box, .gsc-control-cse {
    color: #ccc;
    background-color: #121212;
    background-image: none;
  }
  #I_top {
    background-image: none;
  }
  .C_doc #parameters DT, TABLE.boxed TD, .C_doc .links DD {
    background-color: #343434;
    color: #ccc;
  }
  .C_doc .C_prototype {
    background-color: #343434;
    color: #4af;
  }
  .C_doc h3::before {
    border-radius: 5px;
  }
  .C_doc .links .typ {
    color: #0f0;
  }
  input, select, textarea,
  TABLE.boxed TH, .C_doc DL.links, .C_doc .links DT, .celltop, .C_deco TR.odd TD, .C_deco TR TD, .C_artList TH,
  .C_forPost .boxtop {
    border: solid 0.5px #555;
    background-color: #232323;
    color: #fff;
  }
  .C_doc .links a {
    color: #fff;
  }
  .celltop a, .cell_info, .cell_src, .cell_ref, .cell_doc, .cell_art, .cell_sch {
    color: #ccc;
    background-image: none;
  }
  .auto .source, .auto .output, .C_forPost .dwhat .quote TD.qd {
    color: #fff;
    background-color: #232323;
    border: solid 0.5px #555;
  }
  .auto .source CITE { color: #75715e }
  .auto .source DFN  { color: #fd971f }
  .auto .source VAR  { color: #a6e22e }
  .auto .source KBD  { color: #e6db74 }
  
  #I_main a,
  #I_footer a {
    color: #4af;
  }
  
  #I_nav LI A {
    color: #fff;
  }
  #I_nav LI A:hover {
    background-color: #15a;
  }
  #I_nav .selected A, #I_nav LI A:hover SPAN, #I_nav .selected A:hover, #I_nav .selected A SPAN, #I_nav .selected A:hover SPAN {
    background-color: #26b;
  }
  .C_artList TR:hover td {
    background-color: #343434;
  }
  #I_nav LI A SPAN {
    background-color: #232323;
    border-bottom: solid 0.5px #555;
  }
  .gsc-results .gsc-cursor-box .gsc-cursor-page {
    background-color: transparent;
    color: #4af;
  }
  .gsc-webResult.gsc-result {
    background-color: #121212;
    border: solid 0.5px #333;
  }
  .gsc-webResult.gsc-result:hover {
    background-color: #232323;
    border: solid 0.5px #555;
  }
  .gs-webResult .gs-snippet, .gsc-result-info {
    color: #ccc;
  }
  .gs-webResult.gs-result a.gs-title:link b {
    color: #6bf;
  }
  .gsc-tabHeader.gsc-tabhActive {
    background-color: #121212;
    color: #ccc;
    border-bottom: none;
    border-color: #555;
    border-bottom: solid 5px #121212;
  }
  .gsc-tabHeader.gsc-tabhInactive {
    background-color: #232323;
    border-right: solid 0.5px #555;
    color: #ccc;
  }
  .gsc-above-wrapper-area {
    border-bottom-color: #555;
  }
  .gs-result img.gs-image, .gsc-control-cse {
    border: none;
  }
  .C_forThread .box {
    border: none;
    border-bottom: solid 0.5px #555;
    margin-top: 5px;
    margin-bottom: 5px;
  }
}
