css:
@-moz-document url-prefix("http://chat.thecolorless.net/index.html"), url-prefix("http://chat.thecolorless.net/compact.html") {

.subscription li {
    margin-bottom: 10px;
  }
  
.subscription .article {  
  -webkit-border-radius: 5px;
  -moz-border-radius: 5px;
  border-radius: 5px;
  margin-left: 12px;
  margin-bottom: 10px;
  border: 0px solid #fff;
  padding: 15px;
  min-height: 1.0em;
  max-width: 475px;
  
  }

.subscription .triangle {
  visibility: hidden;
  }
  
.subscription address > a {
    text-align: left;
    overflow: visible
  }
  
.subscription address .avatar {
    width: 48px;
    height: 48px;
    border: 0px solid #fff;
   }

.subscription address .avatar img {
    border-radius:5px;
  }
  
.subscription address .avatar:hover {
    background-color: transparent;
  }

.subscription address .avatar a {
    width: 45px;
    top: 0px;

  }
.subscription address .avatar a[data-kick] {
    bottom: 0px;
}

.symbol-nsfw {
  border: 0px;
  padding:0px;
  padding-right: 2px;

  }
  
.subscription .nsfw .article {
  -webkit-box-shadow:inset 0px 0px 0px 2px #DF6447 !important;
  -moz-box-shadow:inset 0px 0px 0px 2px #DF6447 !important;
  box-shadow:inset 0px 0px 0px 2px #DF6447 !important;
  }
  
#radio {
  visibility:hidden;
}
}
