css:
@-moz-document domain("www.slideshare.net") {
/* slide width 100% */
/* スライドの幅を100%に */
#main-panel {
	width: 100%;
	border: none;
}

/* adjust "open description" link */
/* 「descriptionを開く」ボタンの配置を調整 */
.slideshow-description-container .description div {
	width: 100%;
}
.slideshow-description-container .description div button::before {
	content: "more";
	margin-right: -4px;
}

/* Recommend panel width 100% (on the bottom) */
/* 関連スライドの幅を100%に (カラム落ちで自動的にフッタ手前へ配置される) */
#side-panel {
	border: none;
	width: 100%;
}
#related-tab-content .related-ad,
#related-tab-content .lynda-item {
	display: none;
}
}
