css:
@-moz-document regexp(".*") {

* {
    font-family: 'Courier New' !important;
    color: black !important;
    background: white !important;
}

::selection {
    background: #9cf !important;
}

a {
    color: #00f !important;
}

*:after, *:before {
    content: '' !important;
}

img {
    display: none !important;
}

}
