css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("know.namics.com") {
/* Ausrichtung Homepage */
.tabletitle { width:auto !important; }
#flickr_badge_uber_wrapper {margin-top:-5px !important;padding:15px 15px 15px 15px !important; background-color:#E8E8E8 !important; width:100% !important;}
#flickr_badge_wrapper {border:none !important;}
.flickr_badge_image {padding-top:15px !important; padding-left:15px !important; padding-bottom:10px !important;}
#flickr_badge_image9 {padding-right:15px !important;}
a[href="http://www.flickr.com/groups/namics/"] {display:none !important;}
.rssMacro {margin-left:15px !important;}
.rssMacro table {margin:0 !important; }
/* Flex-Layout News */
.news-panel {width:49.85% !important;}
.news-panel .date, .news-panel .blogpost {width:inherit !important;float:none !important;line-height:1.5em;}
.news-panel .user-pic {margin-right:10px !important;}
.news-panel .news-links li a {font-weight:normal !important;}
/* Links unterstreichen */
.wiki-content p a, .wiki-content li a, .confluenceTable a  {text-decoration:underline !important;}
.heatmap li a {text-decoration:none !important;}
}
