css:
@-moz-document domain("meridiannext.com") {
body
{
background-color: darkgrey;
}

a, a:visited, a:hover, a:focus
{
color: #600;
text-shadow: 0 0 8px pink;
}

#content img.alignleft, #content img.alignright, #content img.aligncenter, #content img.alignnone, #content .wp-caption img, .tempera-image-three .entry-content img[class*='align'], .tempera-image-three .entry-summary img[class*='align']
{
border: none;
filter: drop-shadow(6px 6px 2px rgba(0,0,0,0.5));
}

#body-wrapper
{
background: black none;
}

.sidey .widget-title
{
color: darkgrey !important;
color: #444 !important;
}
#wrap
{
background-color: lightgrey;
color: #444 !important;
}
#page-body
{
background-color: rgba(0,0,0,0.5);
}

.post h3
{
color: darkgrey !Important;
}

#main
{
background-color: lightgrey;
color: #444;
}

#access, #nav-toggle
{
opacity: 0.65;
}

.theme-default .nivo-controlNav, .theme-default .nivo-directionNav a, #footer2
{
background-color: darkgrey;
color: white;
opacity: 0.75;
}

h1, h3, h4, h5, h6, #content h1.entry-title, #content h2.entry-title
{
color: #444 !important;
}

a.continue-reading-link, #cryout_ajax_more_trigger
{
background-color: #666;
opacity: 0.85;
}

.sidey .widget-container
{
opacity: 0.75;
}

}
