css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bugbountyforum.com") {

  body {
    background-color: #101010 !important;
  }
  
  .navigation {  
    background-color: #101010 !important;
  }
  
  [class*="column-"] .block.dark {
    background-color: #333 !important;
  }
  
  .center .main h1, .center .main h2, .center .main h3, .center .main h4, .center .main h5, .center .main h6 {
    color: #555;
  }
  
  a, a:visited, :hover {
    color: #888;
  }
  
}
