css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("paragonie.com") {
  body, html, .wallpaper, .feature, .form, .blog-post-body {
    background-color: #010101;
    color: #888;
  }

  .blog-post-body h1, .blog-post-body h2, .blog-post-body h3 {
    color: inherit;
  }

  blockquote {
    border-left: 5px solid #010101;
  }
  
  pre, code, .hljs {
    background-color: #ccc;
    
  }
}
