css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://play.google.com/music/listen") {
	#playlist-drawer .playlist-drawer-item { height: 2em }
	.paper-toolbar-1,
	.paper-toolbar-1 .toolbar-tools.paper-toolbar {
		height: auto;
	}
	
	#auto-playlists-header, #recent-playlists-header, #playlists-header {
		margin-top: .5em;
		margin-bottom: .3em;
	}
	
	#playlist-drawer .autoplaylist-section,
	#playlist-drawer #recent-playlists-container {
		margin-top: 0;
		margin-bottom: 0;	
	}
	
	#playlist-drawer #mainPanel iron-icon,
	#playlist-drawer #mainPanel sj-play-button {
		top: 2px;
	}
	
	#playlist-drawer .playlist-drawer-item .playlist-wrapper { padding-left: 47px }
}
