css:
@-moz-document domain("web.telegram.org") {
/*
  Restyling of Telegram webapp.

  Source code:
  https://userstyles.org/styles/141620/greymazing-telegram
  https://github.com/sbriskin/userstyles.org
*/

.tg_head_split, .tg_head_logo_dropdown:hover, .dropdown:hover, .tg_head_btn:hover, .dropdown-toggle:hover, .dropdown.open .tg_head_btn, .tg_head_btn:hover, .md_modal_head {
    background: #808080 !important;
}

.im_dialogs_scrollable_wrap .active a.im_dialog, .im_dialogs_scrollable_wrap .active a.im_dialog_selected {
    background-color: #808080 !important;
}

.im_dialogs_col_wrap, .im_bottom_panel_wrap {
    background: #DCDCDC !important;
}

.im_history_col_wrap, .composer_rich_textarea, .im_dialogs_search, .im_page_split {
    background: #F2F2F2 !important;
}

.page_wrap {
    background: #CECECE !important;
}
}
