css:
@-moz-document domain("aeon.co") {
/* article and front page stuff*/

article,
.article__wrapper,
.article__wrapper > p,
.article__wrapper > p > p,
.article__wrapper > div,
body > header.header.header--fixed.header--fixed--visible,
body > div.article__related,
footer,
body > div,
body,
.video__content,
.article__inline-sidebar,
.article__body__meta,
.article__body__author-details,
.article__body__author-details *,
header,
header *,
h1,
/*question popup sidebar*/

.inline-question,
.inline-question *,
inline-question__wrapper,
inline-question__wrapper *,
.button-toggle {
    background-color: rgba(0, 0, 0, 0) !important;
    background: #1d1d1d !important;
    color: #bfbfbf !important;
}
.article-card__title {
    background: rgba(0, 0, 0, 0) !important;
    color: #ffffff !important;
}
/*authors' names on front page*/

.article-card__authors {
    color: #bfbfbf !important;
}
.follow-button,
.follow-button *,
.follow-button__container *,
.follow-button__container,
.article__body__author-details__follow,
.article__body__author-details__follow * {
    background: #1d1d1d !important;
    color: #bfbfbf !important;
    background-color: rgba(0, 0, 0, 0) !important;
}
.close-cover {
    background-color: rgba(0, 0, 0, 0.7);
}
.pullquote {
    color: #5193ef !important;
}
.save {
    color: #de1b1b!important;
}
}
