css:
@-moz-document domain("rs.n1info.com"), domain("hr.n1info.com"), domain("ba.n1info.com") {
body,
.single-article .video-source {
    background: #2d2d2d !important;
    border: none !important;
}
.form-control {
    background: #202020 !important;
}
.newsSlider,
.ticker-on-top,
.mainContent,
.content,
.nav-second,
.breadcrumb,
.video-live-wrapper {
    background: #202020 !important;
    border: none !important;
}
.breadcrumb,
.well,
.full-width-title {
    background: #333333 !important;
    border: none !important;
}
.single-article .single-article-title,
.single-article-content .story-lead,
.single-article-content p,
.table-list table th,
.comment-unit .comment-text,
.comment-unit .comment-author,
.social-wrapper .counter,
.socialWrapper .counter,
.one-big-two-medium .media .icon-and-text .media-icon,
.full-width-title .titleText,
.breadcrumb .active span,
.info-and-social-header .pub-date,
.info-and-social-header .font-sizer li,
.tags-content span,
.media .media-heading a,
.bottomNavigation .navbar-nav td > a,
.footer .nav > li > a,
.twoBthreeS .media .mediaDesc a,
.oneBwithList .list-group-item h3 a,
.oneSWithList .secondItem .mediaDesc a,
.media .media-desc a,
.four-medium-list .list-group-item h3 a,
.nav-second .navbar-nav > li > a,
.twoBthreeS .media .mediaDesc a,
.twoMedium .media .mediaDesc a,
.ticker-on-top .ticker-news-title a,
.pubDate time,
.icon-and-text .media-icon-wrapper.icon-left .media-icon,
.oneSWithList .mediaDesc,
.author-box,
.footer .footerContent .copyright,
.twoBthreeS .mediaIcon,
.form-control,
strong,
.linked_news a {
    color: #e6e6e6 !important;
}
.rel_item a {
    color: #000000 !important;
}

/* Linked news */
.linked_news,
.linked_news ul,
.linked_news li {
    background: #2d2d2d !important;
    border: none !important;
}

/* Scroll */
*::-webkit-scrollbar {
    background: #2d2d2d !important;
}
::-webkit-scrollbar-thumb {
    background: #202020 !important;
}
::-webkit-scrollbar-thumb:hover {
    background: #022d4e !important;
}
}
