css:
@-moz-document domain("torrentproject.se") {

/* General colors to overwrite */
body{
	color: #BBB;
    background: #222;
}
#main_content{
	color: #BBB;
}
a{
    color: #2ED03E;
}
a:visited{
	color: #AD4AD2;
}

/* Reset the logo font color*/
#logo_{
	color: white;
}
.liost input{
	background: #444;
}

/* Links on the index site */
a.gb4:visited{
     color: #2ED03E !important;
}

a.gb4 {
    text-decoration: none;
    color: #5a96e5 !important;
}

ul a.gb4:visited{
    color: #AD4AD2 !important;
}

/* Links in the ttorrent listing*/
#similarfiles div > span:first-child > a {
	color: #2ED03E !important;
}


/* */
#similarfiles div{
   border-bottom: 1px solid #4c6143;
}

.gac_bb{
   background: #4c6143;
   color: #cacaca;
}

/* Bottom Nav*/
a:link{
    color: #2ED03E;
}
.fl{
    border: 1px solid #2ED03E;
}
.cur{	
    color: #2ED03E;
    border: 3px solid #2ED03E;
}

div.torrent_cont {
    color: #ffffff;
}

div.ttracker a:link, div.ttracker a:visited, div.ttracker a:active {
    color: #fabfbf;
    text-decoration: none;
}

.torrent-warning, .about {
    text-shadow: 0 1px 0 #7c39ba;
}

.file_size_search {
    color: #e38080;
}
 
}
