css:
@-moz-document url-prefix("http://www.goatlings.com/trades/viewtrade") {
.trade center{
    font-size:11px;
}

a[href*="acceptoffer"] {
    display:inline-block;

    padding: 10px 3px;
    background-color: #9cffcc; /* Button color! */
    border: 3px solid #60ca93; /* Button outline color! */
    border-radius: 5px;
    color: #1c8f53; /* Font color! */
    letter-spacing: 1px;
    font-size: 12px;
}

a[href*="acceptoffer"]:hover { 
    background-color: #44b77b; /* Button color! */
    border: 3px solid #128449; /* Button outline color! */
    color: #066031; /* Font color! */
    box-shadow: 2px 2px #ccc;
    -moz-transition:background .1s ease-in,border .1s ease-in,box-shadow .1s ease-in;
    -o-transition:background .1s ease-in,border .1s ease-in,box-shadow .1s ease-in;
    -webkit-transition:background .1s ease-in,border .1s ease-in,box-shadow .1s ease-in;
}

a[href*="rejectoffer"] {
    display:inline-block;
    padding: 10px 3px;
 background-color: #ffbabe; /* Button color! */
    border: 3px solid #ec6b73; /* Button outline color! */
    border-radius: 5px;
    color: #d93f49; /* Font color! */
    letter-spacing: 1px;
    font-size: 12px;

}

a[href*="rejectoffer"]:hover { 
    background-color: #ec6b73 !important; /* Button color! */
        color: #790e15;
    border: 3px solid #bb323a !important; /* Button outline color! */
    box-shadow: 2px 2px #ccc;
    -moz-transition:background .1s ease-in,border .1s ease-in,box-shadow .1s ease-in;
    -o-transition:background .1s ease-in,border .1s ease-in,box-shadow .1s ease-in;
    -webkit-transition:background .1s ease-in,border .1s ease-in,box-shadow .1s ease-in;
}
}
