css:
@-moz-document domain(9anime.to) {
/*
	adblock and other elements
*/

#header,
.body-wrapper:nth-child(3),
.notification,
hidden-xs,
.jw-media jw-reset,
col-lg-7 col-sm-24 sidebar,
div.sticky,
sticky-left,
sticky-right,
.a_d text-center col-lg-24 col-xs-12,
.mb20,
.a_d,
.mgbox,
#M223269ScriptRootC94435,
#hotest,
#comment,
#footer,
#pop-request,
#fb-root,
script:nth-child(1),
.home-socials,
div.widget:nth-child(2),
.addthis_native_toolbox,
.alert-primary {
    display: none !important;
}
.watchpage {
    background-color: #16151c;
}
.col-md-17,
.jw-media,
.jw-reset,
#player {
    margin-left: auto;
    margin-right: auto;
}
.mt20 {
    margin: 10px;
}
.mt10 {
    margin-top: 0px;
}
.list-link .links .item a {
    color: #9a9a9a;
    font-weight: 500;
    font-size: 1.3rem;
}
#player {
    width: 100%!important;
    height: 100%!important;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 10000;
}
#control {
    margin-left: 100px;
    position: fixed;
    top: 8px;
    z-index: 10002;
    opacity: 0;
    transition: all 1s ease, transform 50ms ease;
    left: 40%;
    transition-delay: 2s;
    width: 200px!important;
    background-color: rgba(0, 0, 0, 0)!important;
    /*
    width: 100%!important;
    height: 3%!important;
    position: fixed;
    top: 97%;
    left: 0%;
    z-index: 10000;
    background-color: rgba(0, 0, 0, 100)!important;

    margin-left: auto;
    margin-right: auto;
    color: blue;
    */
}
html:hover #control {
    opacity: 1;
    transition-delay: 0s !important;
}
div.jw-controlbar.jw-background-color.jw-reset {
    background-color: rgba(0, 0, 0, 0)!important;
    opacity: 0.7;
}
