div#control .item:nth-child(5),
div#control .item:nth-child(8),
div#control .item:nth-child(9),
div#control .item:nth-child(10),
div#control .item:nth-child(11),
div.item.mbtn.report.pull-right,
a.item.mbtn.download.movie.pull-right,
div.widget:nth-child(2) > .widget:nth-child(3) {
    display: none !important;
