css:
/* AGENT_SHEET */
scrollbar > slider {background-color: #c1c1c1 !important; } /* Фон #c1c1c1*/
scrollbar > slider > thumb { -moz-appearance: none !important; border: none !important; background-color: #1883d7!important;; } /* Не активна */
scrollbar > slider > thumb:hover { background-color: #1c97f8; } /* При наведении */
scrollbar > slider > thumb:active { background-color: #62baff; } /* При нажатии */

/* Выделенный текст */
::-moz-selection {
background-color: #62baff !important;
color: #000000 !important;
}
