@-moz-document domain("trello.com") {
  /* General rounding operations */
  div
  {
      border-radius: 13px !important;
  }
  a
  {
      border-radius: 13px !important;
  }

  /* We need to round the user picture */
  .member-avatar
  {
      border-radius: 13px !important;
  }
  .member
  {
      border-radius: 13px !important;
  }

  /* Here we are making sure the card titles don't get rounded off and thus cut off */
  .list-card-title
  {
      border-radius: 0px !important;
  }
  /* Here we are making sure the header doesn't get rounded off cuz it doesn't look good up there */
  #header
  {
      border-radius: 0px !important;
  }
	/* This part is optional for most users but it will make the trello chat box not rounded */
  .trello-chat-popup > .header
  {
      border-radius: 13px 13px 0px 0px !important;
  }
  .trello-chat-popup > .body
  {
      border-radius: 0px !important;
  }
  .trello-chat-popup > .footer
  {
      border-radius: 0px 0px 13px 13px !important;
  }
}
