css:
@-moz-document domain('disqus.com'), url-prefix('https://disqus.com') {

.post-content .post-message p {
    color: white;
}
.post-body {
    background: rgba(0, 0, 0, 0.9);
    padding: 10px;
    border-radius: 10px;
    width: auto;
    display: inline-block;
    margin: 10px;
    box-shadow: 0 0 15px black;
}
.post {
    background: #330000;
    padding: 10px;
    margin: 10px;
    border-radius: 10px;
    transition: background 1s;
}
.post:hover {
    background: #9a1515;
}
.avatar {
    background: black;
    padding: 5px;
    border-radius: 5px;
    margin: 10px;
}
.post-content {
    margin: 0;
}
.post .children {
    background: white;
    border-radius: 10px;
}
.children .post {
    margin: 0 !important;
    padding: 10px !important;
    background: #4c1717 !important;
    border-radius: 5px !important;
    box-shadow: 0 0 15px black;
    transition: background 1s;
}
.children .post:hover {
    background: white !important;
}
.textarea-wrapper--top-level .textarea-wrapper {
    margin-left: 80px;
}
}
