css:
@-moz-document domain("workflowy.com") {
.noted > .notes > .content {
	font-family: "Andale Mono", "Terminal", monospace;
  color: lightslategray;
}
.contentItalic {
	color: goldenrod;
}
.content > .contentLink {
	color: deepskyblue;
}
.done .content,
.done .contentItalic,
.done .contentLink{
	color: seagreen;
}
.content .contentTag,
.contentTag .contentItalic {
	color: coral;
}
.content .contentTag:hover,
.contentTag:hover .contentItalic{
	color: #fff;
  background-color: coral;
}
}
