/*// Can You Run It in the Dark? ////////////////////////////*/


@-moz-document regexp("http(s)?://.*\\.systemrequirementslab\\.com/(?!(v[0-9].[0-9]/)).*") {    

html, body {
    background-color: #181818 !important;
}

body * {
 border-color: #222 !important; 
 color: #888 !important;
 background-color: transparent !important;
}
   
.tipue_drop_box {
    background-color: #222 !important;
}
  
#instruction-dialog {
    background-color: #222 !important;
}
  
div#norequirements {
    background-color: #222 !important;
}
  
.ui-corner-all, .ui-corner-bottom, .ui-corner-right, .ui-corner-br {
    background-color: #222 !important;
}
  
.ui-widget-content {
    background: #222;
}
    
#index_drop_input, #analysis_drop_input {
    border: 1px solid #444 !important;
    background: #222 url('https://s3.amazonaws.com/content.systemrequirementslab.com/cyri/assets/images/search-large.png') no-repeat 15px 15px;
}

}
