css:
@-moz-document domain("app.asana.com") {
  
  .lunaui-board-center-pane-container #center_pane__contents {
  background-color: #eaeaea;
}

.BoardCard {
  background: #ddd none repeat scroll 0 0;
  border-radius: 0px;
  box-shadow: none;
  padding: 10px;
  border: 1px solid #ccc;
}
  
  .BoardCard-name {
  font-size: 14px;
  font-weight: 500;
  line-height: 19px;
}
  
  .BoardCard.BoardCard--hover {
  box-shadow: none;
  transform: none !important;
  opacity: 0.7 !important;
}
  
  .BoardColumnHeader.BoardColumn-header {
  background-color: #777;
  color: #fff;
  padding-left: 10px;
}


.BoardColumnHeader-dropdownButton {
  color: #ffffff !important;
  fill: #ffffff !important;
}
  
  .Sidebar {
  background-color: #555;
}
}
