css:
@-moz-document domain("changingminds.org"), domain("creatingminds.org") {
#table2,
body > table:nth-child(5),
#google_translate_element,
font[color="#FFFFCC"],
body > table:nth-child(4) > tbody > tr:nth-child(2) > td:nth-child(1),
body > table:nth-child(4) > tbody > tr:nth-child(2) > td:nth-child(2),
body > table:nth-child(4) > tbody > tr:nth-child(2) > td:nth-child(4),
body > table:nth-child(4) > tbody > tr:nth-child(2) > td:nth-child(5),
body > table:nth-child(4) > tbody > tr:nth-child(2) > td:nth-child(6),
body > table:nth-child(9) > tbody > tr:nth-child(2) > td > table,
body > table:nth-child(7) {
	display: none !important;
}

* {
	font-family: "Mercury SSm A", "Mercury SSm B", Georgia, serif !important;	
    font-size: 102% !important;
}

body > div:nth-child(3),
body > a > table:nth-child(9) {
	width: 50% !important;
    margin-left: 25%;
}

a:hover {
	text-decoration: none !important;
}

h1 {
	font-size: 2.1em !important;
    color: #111 !important;
}

h2, h2 font {
	font-size: 1.5em !important;
    color: #111 !important;
    margin-top: 30px;
}

h3, h3 font {
	font-size: 1.3em !important;
    color: #111 !important;
    margin-top: 20px;
}

input[name="q"],
input[name="sa"] {
	font-size: 75% !important;
    padding: 3px 7px !important;
    border: none;
    background-color: #f7f7f7;
}

#table11 td[width="18%"] {
	width: auto !important;
}

#table6 td[width="12%"] {
	width: auto !important;
}

body > table:nth-child(4) > tbody > tr:nth-child(2) > td:nth-child(3),
body > a > table:nth-child(6) > tbody > tr > td {
	padding: 5% 20% !important;
}

body > table:nth-child(4) > tbody > tr:nth-child(2) > td:nth-child(3) a,
body > table:nth-child(4) > tbody > tr:nth-child(2) > td:nth-child(3) p,
body > table:nth-child(4) > tbody > tr:nth-child(2) > td:nth-child(3) li,
body > a > table:nth-child(6) > tbody > tr > td p {
	font-size: 20px !important;
}

body > a > table:nth-child(6) > tbody > tr > td p.smaller {
	font-size: 16px !important;
}

table p,
body > table:nth-child(4) > tbody > tr:nth-child(2) > td:nth-child(3) p {
	text-align: justify !important;
}

p,
ul li,
#AutoNumber3 > tbody > tr:nth-child(2) > td:nth-child(2) a,
#AutoNumber3 > tbody > tr:nth-child(2) > td:nth-child(3) a {
	line-height: 1.9em !important;
}

hr {
    display: block; 
    height: 1px;
    border: 0; 
    border-top: 1px solid #ddd;
    margin: 1em 0; 
    padding: 0;	
}

a,
a:active,
a:visited {
	color: #800000 !important;
}

a:hover {
	text-decoration: underline;
}

table,
table tr,
table tr td,
#table12,
body > table:nth-child(9) {
	background: #fff !important;
    color: #111 !important;
    border-color: #fff !important;
}

a.butn20,
a.butn40,
a:hover.butn20,
a:visited.butn40 {
	background: #fff !important;
    color: #111 !important;
    font-weight: normal !important;
}

#table1 p {
    padding: 10px !important;
	background: #fff !important;
}

#table1 p font {
        color: #111 !important;
}

#table1 p > font[color="#D0E4F8"] {
	color: #ccc !important;
}

}
