css:
@-moz-document url-prefix("https://kaims.eti.pg.gda.pl/~kmocet/stos/") {
@font-face {
    font-family: 'ComicReliefBold';
        src: url('http://fontlibrary.org/assets/fonts/comic-relief/45c456b6db2aaf2f7f69ac66b5ac7239/a3f1ed5c00ad1bfe5156b795b52c434c/ComicReliefBold.ttf') format('truetype');
    font-weight: bold;
    font-style: normal;
      }

@font-face {
    font-family: 'ComicReliefRegular';
        src: url('http://fontlibrary.org/assets/fonts/comic-relief/45c456b6db2aaf2f7f69ac66b5ac7239/59b1a86717dcbbd64399336ca91ea401/ComicReliefRegular.ttf') format('truetype');
    font-weight: normal;
    font-style: normal;
      }

  html, body, #panel, .bgbr1, .bgbr2, .bgbr3, .bgbr4, .bgbr5, th, span.menuitem:hover, span.menuitemh {
   font-family: 'ComicReliefRegular'; 
    background-color: #ED34E7;
  }
  
  #leftmenu, .mnbr1, .mnbr2, .mnbr3, .mnbr4, .mnbr5, .bordl, .menublock, table.deftbl tr:nth-child(2n) {
    background-color: #F56EF0;
  }
  
  #content, .cnbr1, .cnbr2, .cnbr3, .cnbr4, .cnbr5, .bordr, td.seprow, table.deftbl tr:nth-child(2n+1) {
    background-color: #FF8AFB;
  }
  
  #content {
    background-repeat: no-repeat;
    background-position: bottom right;
    background-image: url('http://i.imgur.com/sBd675d.png');
  }

  .shbr1, .shbr2, .shbr3, .shbr4, .shbr5, .shbordl, .shbordr, #shadowfill {
    background-color: #D121CB;
  }
  
  @keyframes pre_rotate {
    0% {
      transform: rotate(0deg);
    }
    25% {
      transform: rotate(90deg);
    }
    50% {
      transform: rotate(180deg);
    }
    75% {
      transform: rotate(270deg);
    }
    100% {
      transform: rotate(360deg);
    }
  }
}
