.mblink:visited, a:visited {
  color: #777 !important;
}
a:visited,
.s .vshid a,
.s .vshid a:visited,
.s .std a,
.s .std a:visited{
  opacity: .5 !important;
  text-decoration: none !important;
}

.s .vshid a:hover,
.s .std a:hover{
  color: #666 !important;
  text-decoration: underline !important;
}

div.kv>cite, div.f>cite, div#tads cite, div.kv>.rcct a.fl, li.g div.f .pplsrsl, li.g div.f>span.std>a.fl, li#newsbox span.tl>a, li#newsbox div.vsc>div.gl, div#results h4.r, div#results cite, div.kv>cite, div.f>cite, div#tads cite, div.kv>.rcct a.fl, li.g div.f .pplsrsl, li.g div.f>span.std>a.fl, li#newsbox span.tl>a, li#newsbox div.vsc>div.gl, div#results h4.r, div#results cite {
