}

h3.r a {
  line-height: 23px !important;
}

._rl,
.spell,
h3.r a,
.spell_orig,
[dir="rtl"],
.slp span,
.gsfi, .lst,
._wI, ._wI a,
input[dir="rtl"],
#res a[dir="rtl"],
li.g h3 a[dir="rtl"],
h3.r span[dir="rtl"],
.rllt__details > div,
#rso div.f.kv._SWb > cite,
#imagebox_bigimages a:not(#irl_r_a)  {
